import './App.css';
import { useState } from 'react';
import ReactDOM from 'react-dom/client';
import Axios from 'axios';

function RegForm(){

  const [inputs, setInputs] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(inputs);

    Axios.post('http://localhost:3001/', {name, email, password})
  }


  return (
    <div className="RegForm"> 
      Register a new account:
      <form onSubmit={handleSubmit}>
        <label>Name:
          <input
            type="text"
            name="name"
            value={inputs.name || ""}
            onChange={handleChange}
          />
        </label> 

        <label>{"\n"}Email:
          <input
            type="email"
            name="email"
            value={inputs.email || ""}
            onChange={handleChange}
          />
        </label> 

        <label>{"\n"}Password:
          <input
            type="text"
            name="password"
            value={inputs.password || ""}
            onChange={handleChange}
          />
        </label> 


      </form>
      <button
        title="Register"
        onPress={handleSubmit}
      > Register!
      </button>
    </div>

  )
}
function LoginForm(){

  const [inputs, setInputs] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(inputs);
  }

  return (
    <div className="LoginForm">
      Login to an existing account:
      <form onSubmit={handleSubmit}>
        <label>{"\n"}Email:
          <input
            type="email"
            name="email"
            value={inputs.email || ""}
            onChange={handleChange}
          />
        </label> 

        <label>{"\n"}Password:
          <input
            type="text"
            name="password"
            value={inputs.password || ""}
            onChange={handleChange}
          />
        </label> 


      </form>
      <button
        title="Login"
        onPress={handleSubmit}
      > Login!
      </button>
    </div>

  )
}

function App() {
  return (
    <div className="App">
      <h1>
        Austin Chan - Week 4 - User Reg and Login Test using React, NodeJS, and MySQL
      </h1>
      {RegForm()}

      {LoginForm()}
    </div>
  );
}

export default App;
